import React, { useState } from 'react'
import namedb from './names'

/*
jostain syystä returnin li-elementti ei päivitä listaa.
 clogilla se kuitenkin toimii
*/

const Buttons = ({ amountOrder, toAlphabeticOrder }) => {
    console.log('buttons')
    return (
        <div>
            <button onClick={amountOrder}>Amount order</button>
            <button onClick={toAlphabeticOrder}>Alphabetical order</button>
        </div>
    )
}
const Datarow = ({ person }) => {
    console.log('datarow')
    return (
        <li>
            {person.name} {person.amount}
        </li>
    )
}
const Total = (props) => {
    //return the length of the props list
    return (
        <div>
            Total amount of all names {props.orderedList.length}
        </div>
        )
}
const Search = ({ searchName }) => {
    return(
        <form onSubmit={searchName}>
            <div>
                <button type='submit'>Search</button>
            </div>
        </form>
    )
}


const App = () => {
    const nameList = namedb.names
    const [ orderedList, setOrderedList ] = useState(nameList)
    const [ searchedName, setSearchedName ] = useState('')
    const [ personObject, setPersonObject ] = useState([])




    const amountOrder = () => {
        setOrderedList([])
        /* sorting list of name+amount objects by amount. 
         * b.amount is the amount number. 
         * a.amount > b.amount returns boolean
         * then the sort function check if that's true or false and orders the objects.
         */

        const sortedAmount = nameList.sort((a, b) => (
            a.amount > b.amount ? 1 : -1
        )) 
        setOrderedList(sortedAmount)
    }
    const toAlphabeticOrder = () => {
        setOrderedList([])
        const sortedNames = nameList.sort((a, b) => (
            a.name > b.name ? 1 : -1
        )) 
        setOrderedList(sortedNames)
    }

    const searchName = (event) => {
        console.log('searchname')
        event.preventDefault()
        orderedList.filter(function(p) {
            if(p.name === searchedName) {
                personObject.name = p.name
                personObject.amount = p.amount
                return (<li>moi</li>)
            }
        })
        setPersonObject(personObject)
        return( <div>hei</div>)
    }

    const changeHandler = (event) => {
        setSearchedName(event.target.value)
    }

    console.log('personobj vika', personObject)
    return (
        <div>
            <Buttons amountOrder={amountOrder} toAlphabeticOrder={toAlphabeticOrder}/>
            <ul>
                {orderedList.map((person, index) => 
                    <li key={index}>
                        {person.name} {person.amount}
                    </li>
                )}
            </ul>
            <div>
                <Total orderedList={orderedList}/>
            </div>
            <div>
            <input onChange={changeHandler}/>
            </div>
            <Search searchName={searchName} />
            <div>
                {personObject.name} {personObject.amount}
            </div>
        </div>
  )
}

export default App
