import ReactDOM from 'react-dom';
import React, { useState } from 'react'
import namedb from './names'



const Total = (props) => {
    //return the length of the props list
    return (
        <div>
            Total number of names {props.sortedList.length}
        </div>
    )
}
const Buttons = ({ toAmountOrder, toAlphabeticOrder }) => {
    return (
        <div>
            <button onClick={toAlphabeticOrder}>Sort by name</button>
            <button onClick={toAmountOrder}>Sort by amount</button>
        </div>
    )
}
const Titles = () => {
    return(
        <div id='titles'>
            <div id='nameTitle'>name</div>
            <div id='amountTitle'>amount</div>
        </div>
    )
}
const Datarow = ({ person }) => {
    return (
        <div className='datarow'>
            <li>{person.name}</li>
            <li id='amount'>{person.amount}</li>
        </div>
    )
}
const Search = ({ searchName }) => {
    return(
        <form onSubmit={searchName}>
            <div>
                <button type='submit'>Search</button>
            </div>
        </form>
    )
}


const App = () => {
    //OG lista databasesta.
    const nameList = namedb.names

    //lista joka näkyy UIssa. aluksi OG lista ennen kuin sitä halutaan järjestellä.
    const [ sortedList, setSortedList ] = useState(nameList)

    //haku inputtiin kirjoitettu nimi
    const [ searchedName, setSearchedName ] = useState('')

    //haettu nimi, joka tulee UIhin näkyviin. 
    const [ personObject, setPersonObject ] = useState([])
    const [ errorMessage, setErrorMessage ] = useState(null)


    const toAmountOrder = () => {
        /* sorting list by amount. 
         * b.amount is the amount number. 
         * a.amount > b.amount returns boolean
         * then the sort function check if that's true or false and sorts the objects.
         */

        const sortedAmount = nameList.sort((a, b) => (
            a.amount > b.amount ? 1 : -1
        )) 
        setSortedList(sortedAmount)
        console.log('list is sorted by amount: ', sortedAmount)
        ReactDOM.render(
            <App />,
            document.getElementById('root')
          )    
    }


    const toAlphabeticOrder = () => {
        /* tyhjennetään lista.
         * käytetään samaa sorttaus-funktiota kuin yllä.
         * asetetaan järjestetty lista UIssa näkyväksi listaksi.
         */

        const sortedNames = nameList.sort((a, b) => (
            a.name > b.name ? 1 : -1
        )) 
        setSortedList(sortedNames)
        console.log('list is sorted by name: ', sortedNames)
        ReactDOM.render(
            <App />,
            document.getElementById('root')
          )  
    }


    const searchName = (event) => {
        /* etsitään listasta haluttu nimi ja
         * asetetaan personObjectille haetun henkilön nimi ja amount.
         */
        event.preventDefault()
        let found = false
        sortedList.filter(function(p) {
            if(p.name === searchedName) {
                personObject.name = p.name
                personObject.amount = p.amount
                found = true
                setPersonObject(personObject)
                setErrorMessage('')
            }
            else if (found === false) {
                 setErrorMessage('Name not found')
                 setPersonObject([])
            }
            return(errorMessage, personObject)
        })
    }

    const changeHandler = (event) => {
        setSearchedName(event.target.value)
    }

    return (
        <div>
            <h1>People</h1>
            <div id='listArea'>
                <div id='total'>
                    <Total sortedList={sortedList}/>
                </div>
                {console.log('buttons')}

                <Buttons toAmountOrder={toAmountOrder} toAlphabeticOrder={toAlphabeticOrder}/>
                <Titles/>
                {console.log('sortedList: ', sortedList)}
                <ul>
                    {sortedList.map((person, index) => 
                        <Datarow person={person} key={index}/>
                    )}
                </ul>
            </div>
            <div id='search'>
                <div id='info'>search person by name</div>
                <input onChange={changeHandler} placeholder='Write name'/>
                <Search searchName={searchName} />
                <div id='showSearch'>{personObject.name} {personObject.amount}</div>
                <div id='errorMessage'>{errorMessage}</div>
            </div>
        </div>
    )
}

export default App
